import React, { useState } from "react";
import "./GoalForm.css";
import Button from "../../UI/Button/Button";

const GoalForm = (props) => {
  const [goalValue, setGoalValue] = useState("");

  const goalHandler = (event) => {
    setGoalValue(event.target.value);
    console.log(goalValue);
    // const newGoal = {id : Math.floor(Math.random() * 10), value : e.target.value}
    // const newArr = [newGoal, ...props.items];
  }

  const goalSubmit = (event) => {
    event.preventDefault();
    props.onAddGoal(goalValue);
  }

  return (
    <section id="goal-form">
      <form onSubmit={goalSubmit}>
        <div className="form-control">
          <label>Course Goal</label>
          <input type="text" onChange={goalHandler}></input>
        </div>
        <Button/>
      </form>
    </section>
  );
};

export default GoalForm;
