import React, { useState } from "react";
import "./GoalForm.css";
import Button from "../../UI/Button/Button";

const GoalForm = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isValid, setIsValid] = useState(true);

  const goalInputChangeHandler = (event) => {
    setIsValid(true);
    setEnteredValue(event.target.value);
    // const newGoal = {id : Math.floor(Math.random() * 10), value : e.target.value}
    // const newArr = [newGoal, ...props.items];
  }

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if(enteredValue.trim().length === 0){
      setIsValid(false);
      return; // return 으로 중단
    }
    props.onAddGoal(enteredValue);
  }

  return (
    <section id="goal-form">
      <form onSubmit={formSubmitHandler}>
        <div className="form-control">
          <label style={{color: !isValid ? 'red' : 'black'}}>Course Goal</label>
          <input type="text" style={{borderColor: !isValid ? 'red' : 'black', backgroundColor: !isValid ? 'salmon' : 'transparent'}} onChange={goalInputChangeHandler}></input>
        </div>
        <Button/>
      </form>
    </section>
  );
};

export default GoalForm;
